import { useRef } from "react";

export function useOnDraw() {
  const canvasRef = useRef(null);

  function setCanvasRef(ref) {
    if (!ref) return;
    canvasRef.current = ref;
    initMouseMoveListener();
  }
  function initMouseMoveListener() {
    const mouseMoveListener = (e) => {}
  }
  return setCanvasRef;
}
